let currentSlide={camera1:0,camera2:0,salone:0};function showSlide(e,t){var r=document.querySelectorAll("."+t+" .galleria-item"),l=document.querySelectorAll("."+t+" .indicator"),r=(e>=r.length?currentSlide[t]=0:currentSlide[t]=e<0?r.length-1:e,100*-currentSlide[t]);document.querySelector("."+t+" .galleria-inner").style.transform=`translateX(${r}%)`,l.forEach((e,r)=>{r===currentSlide[t]?e.classList.add("active"):e.classList.remove("active")})}function nextSlide(e){showSlide(currentSlide[e]+1,e)}function prevSlide(e){showSlide(currentSlide[e]-1,e)}document.addEventListener("DOMContentLoaded",()=>{for(var e in currentSlide)showSlide(currentSlide[e],e)});